mapper.select:
SELECT RNO , NAME , STAR , COMU_ID , TITLE , CONTENT FROM REVIEWBOARD
AND RNO = #{ arg0 }
AND NAME LIKE '%' || #{ arg1 } || '%'
AND STAR = #{ arg2 }
ORDER BY RNO DESC OFFSET #{ arg3 } ROWS FETCH NEXT #{ arg4 } ROWS ONLY

SELECT COUNT(*) AS CNT
  FROM REVIEWBOARD


	 AND comu_id = #{ comu_id }


	 AND RNO = #{ rno }


	 AND NAME LIKE '%' || #{name} || '%'


	 AND STAR = #{ star }
