mapper.insert:
INSERT INTO C_RECRUIT(
	 CNO
 , C_NAME
	 GETMAN
 , C_TITLE
 , C_ADDRESS
 , C_INFO
)
VALUES(
	 (SELECT NVL(CNO), 0) + 1 FROM C_RECRUIT)		 
 , #{cno, jdbcType=VARCHAR}
 , #{c_name, jdbcType=VARCHAR}
 , #{c_getman, jdbcType=VARCHAR}
 , #{c_title, jdbcType=VARCHAR}
 , #{c_address, jdbcType=VARCHAR}
 , #{c_info, jdbcType=VARCHAR}
)

mapper.update:
UPDATE C_RECRUIT

 SET
 C_TITLE=#{c_title, jdbcType=VARCHAR}
 C_TITLE=#{c_getman, jdbcType=VARCHAR}
 C_TITLE=#{c_address, jdbcType=VARCHAR}
 C_TITLE=#{c_info, jdbcType=VARCHAR}

 WHERE CNO=#{cno}
